mapper.select:
select P.SECUENCIA, P.DOCUMENTO, P.DOCUMENTO_RESPONSABLE, P.CODIGO_VACUNA, P.FECHA_PRUEBA , P.REALIZADO, P.RESULTADO, P.UUID, P.VIGENCIA_HASTA, P.ESTADO, M.DESCRIPCIONLOCAL as "DESCRIPCIONVACUNA" from sgcoresys.T_MVC_VAC_PRUEBA_DESCARTE P inner join sgcoresys.ma_miscelaneosdetalle M on P.CODIGO_VACUNA = M.CODIGOELEMENTO where aplicacioncodigo = 'HR' and compania = '999999' and codigotabla ='VACUNAS' and P.DOCUMENTO_RESPONSABLE = #{documentoresponsable}
AND P.DOCUMENTO = #{documento}
AND P.CODIGO_VACUNA = #{codigovacuna}
AND P.estado = '1'

select P.SECUENCIA, P.DOCUMENTO, P.DOCUMENTO_RESPONSABLE, P.CODIGO_VACUNA, P.FECHA_PRUEBA , P.REALIZADO, P.RESULTADO, P.UUID, P.VIGENCIA_HASTA, P.ESTADO, M.DESCRIPCIONLOCAL as DESCRIPCIONVACUNA from sgcoresys.T_MVC_VAC_PRUEBA_DESCARTE P INNER JOIN sgcoresys.ma_miscelaneosdetalle M on P.CODIGO_VACUNA = M.CODIGOELEMENTO where aplicacioncodigo = 'HR' and compania = '999999' and codigotabla ='VACUNAS'
AND P.DOCUMENTO_RESPONSABLE = #{documentoresponsable}
AND P.DOCUMENTO = #{documento}
AND P.CODIGO_VACUNA = #{codigovacuna}
AND P.estado = '1'
